er.load(["ext.cite.a11y","site","mediawiki.page.startup","mediawiki.user","mediawiki.hidpi","mediawiki.page.ready","mediawiki.toc","mediawiki.searchSuggest","ext.gadget.teahouse","ext.gadget.ReferenceTooltips","ext.gadget.watchlist-notice","ext.gadget.DRN-wizard","ext.gadget.charinsert","ext.gadget.refToolbar","ext.gadget.extra-toolbar-buttons","ext.gadget.switcher","ext.centralauth.centralautologin","mmv.head","mmv.bootstrap.autostart","ext.popups","ext.visualEditor.desktopArticleTarget.init","ext.visualEditor.targetLoader","ext.eventLogging.subscriber","ext.wikimediaEvents","ext.navigationTiming","ext.uls.eventlogger","ext.uls.init","ext.uls.interface","ext.centralNotice.geoIP","ext.centralNotice.startUp","skins.vector.js"]);});</script> <link rel="stylesheet" href="/w/load.php?debug=false&amp;lang=en&amp;modules=ext.cite.styles%7Cext.uls.interlanguage%7Cext.visualEditor.desktopArticleTarget.noscript%7Cext.wikimediaBadges%7Cmediawiki.legacy.commonPrint%2Cshared%7Cmediawiki.sectionAnchor%7Cmediawiki.skinning.interface%7Cskins.vector.styles%7Cwikibase.client.init&amp;only=styles&amp;skin=vector"/> <script async="" src="/w/load.php?debug=false&amp;lang=en&amp;modules=startup&amp;only=scripts&amp;skin=vector"></script> <meta name="ResourceLoaderDynamicStyles" content=""/> <link rel="stylesheet" href="/w/load.php?debug=false&amp;lang=en&amp;modules=ext.gadget.charinsert-styles&amp;only=styles&amp;skin=vector"/> <link rel="stylesheet" href="/w/load.php?debug=false&amp;lang=en&amp;modules=site.styles&amp;only=styles&amp;skin=vector"/> <meta name="generator" content="MediaWiki 1.31.0-wmf.11"/> <meta name="referrer" content="origin-when-cross-origin"/> <meta property="og:image" content="https://upload.wikimedia.org/wikipedia/commons/2/27/Codex_Gigas_devil.jpg"/> <link rel="alternate" href="android-app://org.wikipedia/http/en.m.wikipedia.org/wiki/Good"/> <link rel="alternate" type="application/x-wiki" title="Edit this page" href="/w/index.php?title=Good&amp;action=edit"/> <link rel="edit" title="Edit this page" href="/w/index.php?title=Good&amp;action=edit"/> <link rel="apple-touch-icon" href="/static/apple-touch/wikipedia.png"/> <link rel="shortcut icon" href="/static/favicon/wikipedia.ico"/> <link rel="search" type="application/opensearchdescription+xml" href="/w/opensearch_desc.php" title="Wikipedia (en)"/> <link rel="EditURI" type="application/rsd+xml" href="//en.wikipedia.org/w/api.php?action=rsd"/> <link rel="license" href="//creativecommons.org/licenses/by-sa/3.0/"/> <link rel="canonical" href="https://en.wikipedia.org/wiki/Good"/> <link rel="dns-prefetch" href="//login.wikimedia.org"/> <link rel="dns-prefetch" href="//meta.wikimedia.org" /> <!--[if lt IE 9]><script src="/w/load.php?debug=false&amp;lang=en&amp;modules=html5shiv&amp;only=scripts&amp;skin=vector&amp;sync=1"></script><![endif]--> </head> <body class="mediawiki ltr sitedir-ltr mw-hide-empty-elt ns-0 ns-subject page-Good rootpage-Good skin-vector action-view">		<div id="mw-page-base" class="noprint"></div> 		<div id="mw-head-base" class="noprint"></div> 		<div id="content" class="mw-body" role="main"> 			<a id="top"></a> 			<div id="siteNotice" class="mw-body-content"><!-- CentralNotice --></div><div class="mw-indicators mw-body-content"> </div> <h1 id="firstHeading" class="firstHeading" lang="en">Good</h1>			<div id="bodyContent" class="mw-body-content"> 				<div id="siteSub" class="noprint">From Wikipedia, the free encyclopedia</div>				<div id="contentSub"></div> 								<div id="jump-to-nav" class="mw-jump"> 					Jump to:					<a href="#mw-head">navigation</a>, 					<a href="#p-search">search</a> 				</div> 				<div id="mw-content-text" lang="en" dir="ltr" class="mw-content-ltr"><div class="mw-parser-output"><div role="note" class="hatnote navigation-not-searchable">For other uses, see <a href="/wiki/Good_(disambiguation)" class="mw-disambig" title="Good (disambiguation)">Good (disambiguation)</a>.</div> <div class="thumb tright"> <div class="thumbinner" style="width:177px;"><a href="/wiki/File:Lenz_Entwurf_zu_einem_Engel.jpg" class="image"><img alt="" src="//upload.wikimedia.org/wikipedia/commons/thumb/0/08/Lenz_Entwurf_zu_einem_Engel.jpg/175px-Lenz_Entwurf_zu_einem_Engel.jpg" width="175" height="484" class="thumbimage" srcset="//upload.wikimedia.org/wikipedia/commons/thumb/0/08/Lenz_Entwurf_zu_einem_Engel.jpg/263px-Lenz_Entwurf_zu_einem_Engel.jpg 1.5x, //upload.wikimedia.org/wikipedia/commons/0/08/Lenz_Entwurf_zu_einem_Engel.jpg 2x" data-file-width="267" data-file-height="739" /></a> <div class="thumbcaption"> <div class="magnify"><a href="/wiki/File:Lenz_Entwurf_zu_einem_Engel.jpg" class="internal" title="Enlarge"></a></div> In many religions, <a href="/wiki/Angel" title="Angel">angels</a> are considered good beings. In the <a href="/wiki/Judeo-Christian" title="Judeo-Christian">Judeo-Christian</a> tradition, <a href="/wiki/God" title="God">God</a>&#160;—being the creator of all life&#160;—is seen as the personification of good.</div> </div> </div> <div class="thumb tright"> <div class="thumbinner" style="width:177px;"><a href="/wiki/File:Codex_Gigas_devil.jpg" class="image"><img alt="" src="//upload.wikimedia.org/wikipedia/commons/thumb/2/27/Codex_Gigas_devil.jpg/175px-Codex_Gigas_devil.jpg" width="175" height="278" class="thumbimage" srcset="//upload.wikimedia.org/wikipedia/commons/thumb/2/27/Codex_Gigas_devil.jpg/263px-Codex_Gigas_devil.jpg 1.5x, //upload.wikimedia.org/wikipedia/commons/thumb/2/27/Codex_Gigas_devil.jpg/350px-Codex_Gigas_devil.jpg 2x" data-file-width="369" data-file-height="586" /></a> <div class="thumbcaption"> <div class="magnify"><a href="/wiki/File:Codex_Gigas_devil.jpg" class="internal" title="Enlarge"></a></div> <a href="/wiki/Satan" title="Satan">Satan</a>, as seen in <a href="/wiki/Codex_Gigas" title="Codex Gigas">Codex Gigas</a>. <a href="/wiki/Demon" title="Demon">Demons</a> are generally seen as evil beings, and Satan as greatest of these (in the Christian tradition).</div> </div> </div> <p>In its most general context of the study of morality, ethics, religion and philosophy, the <b>good</b> often refers to and denotes that conduct which is to be preferred and prescribed by society and its social constituents as beneficial and useful to the social needs of society and its preferred conventions. The specific meaning and etiology of the meaning and use of the 'good' and its associated translations among ancient and contemporary languages has varied substantially in its inflected meaning depending of circumstances of place, history, religious context and philosophical context.</p> <p>A significant enlightenment context for studying the 'good' has been its significance in the study of <b>"the good, the true and the beautiful"</b> as found in <a href="/wiki/Immanuel_Kant" title="Immanuel Kant">Immanuel Kant</a> and other Enlightenment and Renaissance philosophers and religious thinkers. These discussions were undertaken by Kant particularly in the context of his second <a href="/wiki/Critique_of_Practical_Reason" title="Critique of Practical Reason">Critique of Practical Reason</a> within his Three Critiques.</p> <p></p> <div id="toc" class="toc"> <div class="toctitle"> <h2>Contents</h2> </div> <ul> <li class="toclevel-1 tocsection-1"><a href="#Origin_and_history"><span class="tocnumber">1</span> <span class="toctext">Origin and history</span></a> <ul> <li class="toclevel-2 tocsection-2"><a href="#Plato_and_Aristotle"><span class="tocnumber">1.1</span> <span class="toctext">Plato and Aristotle</span></a></li> <li class="toclevel-2 tocsection-3"><a href="#Classical_world"><span class="tocnumber">1.2</span> <span class="toctext">Classical world</span></a></li> <li class="toclevel-2 tocsection-4"><a href="#Ancient_world"><span class="tocnumber">1.3</span> <span class="toctext">Ancient world</span></a></li> <li class="toclevel-2 tocsection-5"><a href="#Medieval_period"><span class="tocnumber">1.4</span> <span class="toctext">Medieval period</span></a></li> <li class="toclevel-2 tocsection-6"><a href="#Modern_ideas"><span class="tocnumber">1.5</span> <span class="toctext">Modern ideas</span></a></li> </ul> </li> <li class="toclevel-1 tocsection-7"><a href="#Theories_of_moral_goodness"><span class="tocnumber">2</span> <span class="toctext">Theories of moral goodness</span></a></li> <li class="toclevel-1 tocsection-8"><a href="#Good_and_evil"><span class="tocnumber">3</span> <span class="toctext">Good and evil</span></a></li> <li class="toclevel-1 tocsection-9"><a href="#Descriptive,_meta-ethical,_and_normative_fields"><span class="tocnumber">4</span> <span class="toctext">Descriptive, meta-ethical, and normative fields</span></a></li> <li class="toclevel-1 tocsection-10"><a href="#Theories_of_the_intrinsically_good"><span class="tocnumber">5</span> <span class="toctext">Theories of the intrinsically good</span></a> <ul> <li class="toclevel-2 tocsection-11"><a href="#Transcendental_realism"><span class="tocnumber">5.1</span> <span class="toctext">Transcendental realism</span></a></li> <li class="toclevel-2 tocsection-12"><a href="#Perfectionism"><span class="tocnumber">5.2</span> <span class="toctext">Perfectionism</span></a> <ul> <li class="toclevel-3 tocsection-13"><a href="#Subjective_theories_of_well-being"><span class="tocnumber">5.2.1</span> <span class="toctext">Subjective theories of well-being</span></a></li> <li class="toclevel-3 tocsection-14"><a href="#Objective_theories_of_well-being"><span class="tocnumber">5.2.2</span> <span class="toctext">Objective theories of well-being</span></a></li> <li class="toclevel-3 tocsection-15"><a href="#Mid-range_theories"><span class="tocnumber">5.2.3</span> <span class="toctext">Mid-range theories</span></a></li> </ul> </li> </ul> </li> <li class="toclevel-1 tocsection-16"><a href="#Goodness_and_agency"><span class="tocnumber">6</span> <span class="toctext">Goodness and agency</span></a> <ul> <li class="toclevel-2 tocsection-17"><a href="#Goodwill"><span class="tocnumber">6.1</span> <span class="toctext">Goodwill</span></a></li> <li class="toclevel-2 tocsection-18"><a href="#Society,_life_and_ecology"><span class="tocnumber">6.2</span> <span class="toctext">Society, life and ecology</span></a></li> <li class="toclevel-2 tocsection-19"><a href="#History_and_novelty"><span class="tocnumber">6.3</span> <span class="toctext">History and novelty</span></a></li> </ul> </li> <li class="toclevel-1 tocsection-20"><a href="#Goodness_and_morality_in_biology"><span class="tocnumber">7</span> <span class="toctext">Goodness and morality in biology</span></a></li> <li class="toclevel-1 tocsection-21"><a href="#See_also"><span class="tocnumber">8</span> <span class="toctext">See also</span></a></li> <li class="toclevel-1 tocsection-22"><a href="#References"><span class="tocnumber">9</span> <span class="toctext">References</span></a></li> <li class="toclevel-1 tocsection-23"><a href="#Further_reading"><span class="tocnumber">10</span> <span class="toctext">Further reading</span></a></li> </ul> </div> <p></p> <h2><span class="mw-headline" id="Origin_and_history">Origin and history</span><span class="mw-editsection"><span class="mw-editsection-bracket">[</span><a href="/w/index.php?title=Good&amp;action=edit&amp;section=1" title="Edit section: Origin and history">edit</a><span class="mw-editsection-bracket">]</span></span></h2> <div role="note" class="hatnote navigation-not-searchable">Further information: <a href="/wiki/Form_of_the_good" class="mw-redirect" title="Form of the good">Form of the good</a>, <a href="/wiki/Origins_of_morality" class="mw-redirect" title="Origins of morality">Origins of morality</a>, and <a href="/wiki/Morality" title="Morality">Morality</a></div> <p>Every language has a word expressing <i>good</i> in the sense of "having the right or desirable quality" (<a href="/wiki/Arete_(moral_virtue)" title="Arete (moral virtue)">ἀρετή</a>) and <i>bad</i> in the sense "undesirable". A sense of <a href="/wiki/Morality" title="Morality">moral judgment</a> and a distinction "right and wrong, good and bad" are <a href="/wiki/Cultural_universals" class="mw-redirect" title="Cultural universals">cultural universals</a>.<sup id="cite_ref-1" class="reference"><a href="#cite_note-1">[1]</a></sup></p> <h3><span class="mw-headline" id="Plato_and_Aristotle">Plato and Aristotle</span><span class="mw-editsection"><span class="mw-editsection-bracket">[</span><a href="/w/index.php?title=Good&amp;action=edit&amp;section=2" title="Edit section: Plato and Aristotle">edit</a><span class="mw-editsection-bracket">]</span></span></h3> <p>Although the history of the origin of the use of the concept and meaning of 'good' are diverse, the notable discussions of Plato and Aristotle on this subject have been of significant historical effect. The first references that are seen in Plato's <i>The Republic</i> to the <a href="/wiki/Form_of_the_Good" title="Form of the Good">Form of the Good</a> are within the conversation between <a href="/wiki/Glaucon" title="Glaucon">Glaucon</a> and <a href="/wiki/Socrates" title="Socrates">Socrates</a> (454 c–d). When trying to answer such difficult questions pertaining to the definition of <a href="/wiki/Justice" title="Justice">justice</a>, Plato identifies that we should not “introduce every form of difference and sameness in nature” instead we must focus on "the one form of sameness and difference that was relevant to the particular ways of life themselves” which is the form of the Good. This form is the basis for understanding all other forms, it is what allows us to understand everything else. Through the conversation between Socrates and Glaucon (508 a–c) Plato analogizes the form of the Good with the sun as it is what allows us to see things. Here, Plato describes how the sun allows for sight. But he makes a very important distinction, “sun is not sight” but it is “the cause of sight itself.” As the sun is in the visible realm, the form of Good is in the <a href="/wiki/Intelligibility_(philosophy)" title="Intelligibility (philosophy)">intelligible</a> realm. It is “what gives truth to the things known and the power to know to the knower”. It is not only the “cause of knowledge and truth, it is also an object of knowledge”.</p> <p>Plato identifies how the form of the Good allows for the cognizance to understand such difficult concepts as justice. He identifies knowledge and truth as important, but through Socrates (508d–e) says, “good is yet more prized”. He then proceeds to explain “although the good is not being” it is “superior to it in rank and power”, it is what “provides for knowledge and truth” (508e).<sup id="cite_ref-Reeve_2-0" class="reference"><a href="#cite_note-Reeve-2">[2]</a></sup></p> <p>In contrast to Plato, <a href="/wiki/Aristotle" title="Aristotle">Aristotle</a> discusses the Forms of Good in critical terms several times in both of his major surviving ethical works, the <i><a href="/wiki/Eudemian_Ethics" title="Eudemian Ethics">Eudemian</a></i> and <i><a href="/wiki/Nicomachean_Ethics" title="Nicomachean Ethics">Nicomachean Ethics</a></i>. Aristotle argues that Plato’s Form of the Good does not apply to the physical world, for Plato does not assign “goodness” to anything in the existing world. Because Plato’s Form of the Good does not explain events in the physical world, humans have no reason to believe that the Form of the Good exists and the Form of the Good is thereby irrelevant to human ethics.<sup id="cite_ref-plat_3-0" class="reference"><a href="#cite_note-plat-3">[3]</a></sup></p> <h3><span class="mw-headline" id="Classical_world">Classical world</span><span class="mw-editsection"><span class="mw-editsection-bracket">[</span><a href="/w/index.php?title=Good&amp;action=edit&amp;section=3" title="Edit section: Classical world">edit</a><span class="mw-editsection-bracket">]</span></span></h3> <p>Plato and Aristotle were not the first contributors in ancient Greece to the study of the 'good' and discussion preceding them can be found among the pre-Socratic philosophers. In Western civilisation, the basic meanings of κακός and ἀγαθός are "bad, cowardly" and "good, brave, capable", and their absolute sense emerges only around 400 BC, with <a href="/wiki/Pre-Socratic_philosophy" title="Pre-Socratic philosophy">Pre-Socratic philosophy</a>, in particular <a href="/wiki/Democritus" title="Democritus">Democritus</a>.<sup id="cite_ref-4" class="reference"><a href="#cite_note-4">[4]</a></sup> Morality in this absolute sense solidifies in the dialogues of <a href="/wiki/Plato" title="Plato">Plato</a>, together with the emergence of <a href="/wiki/Monotheistic" class="mw-redirect" title="Monotheistic">monotheistic</a> thought (notably in <i><a href="/wiki/Euthyphro" title="Euthyphro">Euthyphro</a></i>, which ponders the concept of piety (<a href="https://en.wiktionary.org/wiki/%E1%BD%85%CF%83%CE%B9%CE%BF%CF%82" class="extiw" title="wikt:ὅσιος">τὸ ὅσιον</a>) as a moral absolute). The idea is further developed in <a href="/wiki/Late_Antiquity" class="mw-redirect" title="Late Antiquity">Late Antiquity</a> by <a href="/wiki/Neoplatonists" class="mw-redirect" title="Neoplatonists">Neoplatonists</a>, <a href="/wiki/Gnostics" class="mw-redirect" title="Gnostics">Gnostics</a>, and <a href="/wiki/Church_Fathers" title="Church Fathers">Church Fathers</a>.</p> <h3><span class="mw-headline" id="Ancient_world">Ancient world</span><span class="mw-editsection"><span class="mw-editsection-bracket">[</span><a href="/w/index.php?title=Good&amp;action=edit&amp;section=4" title="Edit section: Ancient world">edit</a><span class="mw-editsection-bracket">]</span></span></h3> <div role="note" class="hatnote navigation-not-searchable">Main article: <a href="/wiki/Zoroastrianism" title="Zoroastrianism">Zoroastrianism</a></div> <p>Aside from ancient Greek studies of the 'good', the eastern part of ancient <a href="/wiki/Greater_Iran" title="Greater Iran">Persia</a> almost five thousand years ago a religious philosopher called <a href="/wiki/Zoroaster" title="Zoroaster">Zoroaster</a> simplified the <a href="/wiki/Pantheon_(gods)" class="mw-redirect" title="Pantheon (gods)">pantheon</a> of early Iranian gods<sup id="cite_ref-Boyce_1979_6-12_5-0" class="reference"><a href="#cite_note-Boyce_1979_6-12-5">[5]</a></sup> into <a href="/wiki/Dualistic_cosmology" title="Dualistic cosmology">two opposing forces</a>: <a href="/wiki/Ahura_Mazda" title="Ahura Mazda">Ahura Mazda</a> (<a href="/wiki/Light_(theology)" class="mw-redirect" title="Light (theology)">Illuminating</a> <a href="/wiki/Wisdom" title="Wisdom">Wisdom</a>) and <a href="/wiki/Angra_Mainyu" title="Angra Mainyu">Angra Mainyu</a> (<a href="/wiki/Devil" title="Devil">Destructive Spirit</a>) which were in conflict.</p> <div role="note" class="hatnote navigation-not-searchable">Main article: <a href="/wiki/Gnosticism" title="Gnosticism">Gnosticism</a></div> <p>For the western world, this idea developed into a religion which spawned many <a href="/wiki/Sect" title="Sect">sects</a>, some of which embraced an extreme <a href="/wiki/Dualistic_cosmology" title="Dualistic cosmology">dualistic</a> belief that the <a href="/wiki/Nature" title="Nature">material world</a> should be shunned and the <a href="/wiki/Sacred" title="Sacred">spiritual world</a> should be embraced. Gnostic ideas influenced many <a href="/wiki/Ancient_history" title="Ancient history">ancient</a> religions<sup id="cite_ref-6" class="reference"><a href="#cite_note-6">[6]</a></sup> which teach that <i><a href="/wiki/Gnosis" title="Gnosis">gnosis</a></i> (variously interpreted as <a href="/wiki/Enlightenment_(spiritual)" title="Enlightenment (spiritual)">enlightenment</a>, <a href="/wiki/Salvation" title="Salvation">salvation</a>, <a href="/wiki/Emancipation" title="Emancipation">emancipation</a> or <a href="/wiki/Buddhahood" title="Buddhahood">'oneness with God'</a>) may be reached by practising <a href="/wiki/Philanthropy" title="Philanthropy">philanthropy</a> to the point of personal <a href="/wiki/Poverty" title="Poverty">poverty</a>, <a href="/wiki/Sexual_abstinence" title="Sexual abstinence">sexual abstinence</a> (as far as possible for <i><a href="https://en.wiktionary.org/wiki/disciple#Noun" class="extiw" title="wikt:disciple">hearers</a></i>, total for <i><a href="/wiki/Initiation" title="Initiation">initiates</a></i>) and diligently searching for <a href="/wiki/Wisdom" title="Wisdom">wisdom</a> by helping others.<sup id="cite_ref-gph_7-0" class="reference"><a href="#cite_note-gph-7">[7]</a></sup></p> <p>This development from the relative or habitual to the absolute is also evident in the terms <i><a href="/wiki/Ethics" title="Ethics">ethics</a></i> and <i><a href="/wiki/Morality" title="Morality">morality</a></i> both being derived from terms for "regional custom", Greek ήθος and Latin <i>mores</i>, respectively (see also <i><a href="https://en.wiktionary.org/wiki/si%C3%B0r" class="extiw" title="wikt:siðr">siðr</a></i>).</p> <h3><span class="mw-headline" id="Medieval_period">Medieval period</span><span class="mw-editsection"><span class="mw-editsection-bracket">[</span><a href="/w/index.php?title=Good&amp;action=edit&amp;section=5" title="Edit section: Medieval period">edit</a><span class="mw-editsection-bracket">]</span></span></h3> <div role="note" class="hatnote navigation-not-searchable">Main article: <a href="/wiki/Christian_philosophy" title="Christian philosophy">Christian philosophy</a></div> <p>Medieval <a href="/wiki/Christian_philosophy" title="Christian philosophy">Christian philosophy</a> was founded on the work of the Bishop <a href="/wiki/Augustine_of_Hippo" title="Augustine of Hippo">Augustine of Hippo</a> and theologian <a href="/wiki/Thomas_Aquinas" title="Thomas Aquinas">Thomas Aquinas</a> who understood evil in terms of <a href="/wiki/Biblical_infallibility" title="Biblical infallibility">Biblical infallibility</a> and <a href="/wiki/Biblical_inerrancy" title="Biblical inerrancy">Biblical inerrancy</a>, as well as the influences of Plato and Aristotle in their appreciation of the concept of the <a href="/wiki/Summum_bonum" title="Summum bonum">Summum bonum</a>. <a href="/wiki/Plato" title="Plato">Plato's</a> <i><a href="/wiki/The_Republic_(Plato)" class="mw-redirect" title="The Republic (Plato)">The Republic</a></i> argued that, “In the world of knowledge the idea of good appears last of all, and is seen...to be the universal author of all things beautiful and right”.<sup id="cite_ref-8" class="reference"><a href="#cite_note-8">[8]</a></sup> Silent contemplation was the route to appreciation of the Idea of the Good.<sup id="cite_ref-9" class="reference"><a href="#cite_note-9">[9]</a></sup></p> <p><a href="/wiki/Aristotle" title="Aristotle">Aristotle</a> in his <i><a href="/wiki/Nicomachean_Ethics" title="Nicomachean Ethics">Nicomachean Ethics</a></i> accepted that the target of human activity, “Must be the 'Good', that is, the supreme good.”, but challenged Plato's Idea of the Good with the <a href="/wiki/Pragmatism" title="Pragmatism">pragmatic</a> question: “Will one who has had a vision of the Idea itself become thereby a better doctor or general?”.<sup id="cite_ref-10" class="reference"><a href="#cite_note-10">[10]</a></sup> However, arguably at least, Aristotle's concept of the <a href="/wiki/Unmoved_mover" title="Unmoved mover">unmoved mover</a> owed much to Plato's Idea of the Good.<sup id="cite_ref-11" class="reference"><a href="#cite_note-11">[11]</a></sup></p> <p>Many medieval Christian <a href="/wiki/Theologian" class="mw-redirect" title="Theologian">theologians</a> both broadened and narrowed the basic concept of <i>Good and evil</i> until it came to have several, sometimes complex definitions<sup id="cite_ref-12" class="reference"><a href="#cite_note-12">[12]</a></sup> such as:</p> <ul> <li>a personal preference or subjective judgment regarding any issue which might be earn <a href="/wiki/Praise" title="Praise">praise</a> or <a href="/wiki/Punishment" title="Punishment">punishment</a> from the <a href="/wiki/Theocracy" title="Theocracy">religious authorities</a></li> <li>religious obligation arising from <a href="/wiki/Divine_law" title="Divine law">Divine law</a> leading to <a href="/wiki/Saint" title="Saint">sainthood</a> or <a href="/wiki/Damnation" title="Damnation">damnation</a>.</li> <li>a generally accepted <a href="/wiki/Norm_(social)" title="Norm (social)">cultural standard</a> of behaviour which might enhance group <a href="/wiki/Human_evolution_(origins_of_society_and_culture)" class="mw-redirect" title="Human evolution (origins of society and culture)">survival</a> or <a href="/wiki/Wealth" title="Wealth">wealth</a></li> <li><a href="/wiki/Natural_law" title="Natural law">natural law</a> or behaviour which induces strong emotional reaction</li> <li><a href="/wiki/State_(polity)" title="State (polity)">statute law</a> imposing a legal <a href="/wiki/Duty" title="Duty">duty</a></li> </ul> <h3><span class="mw-headline" id="Modern_ideas">Modern ideas</span><span class="mw-editsection"><span class="mw-editsection-bracket">[</span><a href="/w/index.php?title=Good&amp;action=edit&amp;section=6" title="Edit section: Modern ideas">edit</a><span class="mw-editsection-bracket">]</span></span></h3> <div role="note" class="hatnote navigation-not-searchable">Main articles: <a href="/wiki/Kant" class="mw-redirect" title="Kant">Kant</a> and <a href="/wiki/Critique_of_Practical_Reason" title="Critique of Practical Reason">Critique of Practical Reason</a></div> <p>A significant enlightenment context for studying the 'good' has been its significance in the study of <b>"the good, the true and the beautiful"</b> as found in Kant and other Enlightenment and Renaissance philosophers and religious thinkers. These discussion were undertaken by <a href="/wiki/Kant" class="mw-redirect" title="Kant">Kant</a> particularly in the context of his second <a href="/wiki/Critique_of_Practical_Reason" title="Critique of Practical Reason">Critique of Practical Reason</a> within his Three Critiques.</p> <h2><span class="mw-headline" id="Theories_of_moral_goodness">Theories of moral goodness</span><span class="mw-editsection"><span class="mw-editsection-bracket">[</span><a href="/w/index.php?title=Good&amp;action=edit&amp;section=7" title="Edit section: Theories of moral goodness">edit</a><span class="mw-editsection-bracket">]</span></span></h2> <p><a href="/wiki/Philosophers" class="mw-redirect" title="Philosophers">Philosophers</a> inquire into what sorts of things are good, and what the word "<a href="/wiki/Good_(religion)" class="mw-redirect" title="Good (religion)">good</a>" really means in the abstract. As a philosophical concept, goodness might represent a <a href="/wiki/Hope" title="Hope">hope</a> that natural love be <i>continuous, expansive,</i> and <i>all-inclusive</i>.<sup class="noprint Inline-Template" style="white-space:nowrap;">[<i><a href="/wiki/Wikipedia:MOSQUOTE" class="mw-redirect" title="Wikipedia:MOSQUOTE"><span title="The text near this tag needs citation. (September 2012)">this quote needs a citation</span></a></i>]</sup> In a monotheistic religious context, it is by this <i>hope</i> that an important concept of <a href="/wiki/God" title="God">God</a> is derived&#160;—as an <a href="/wiki/Infinity" title="Infinity">infinite</a> projection of love, manifest as goodness in the lives of people. In other contexts, the good is viewed to be whatever produces the best consequences upon the lives of people, especially with regard to their states of well being.</p> <div role="note" class="hatnote navigation-not-searchable">For other uses of "good", see <a href="/wiki/Good_(disambiguation)" class="mw-disambig" title="Good (disambiguation)">Good (disambiguation)</a>.</div> <p>As a philosophical abstraction, goodness represents a <a href="/wiki/Hope" title="Hope">hope</a> that natural love be <i>continuous, expansive,</i> and <i>all-inclusive</i>. In religious context, it is by this <i>hope</i> that an important concept of <a href="/wiki/God" title="God">God</a> is derived&#160;—as an <a href="/wiki/Infinity" title="Infinity">infinite</a> projection of love, manifest as goodness in the lives of people. The belief in such hope is often translated as "<a href="/wiki/Faith" title="Faith">faith</a>", and <a href="/wiki/Wisdom" title="Wisdom">wisdom</a> itself is largely defined within religious doctrine as <i>a knowledge and understanding of innate goodness.</i></p> <p>The concepts of <a href="/wiki/Innocence" title="Innocence">innocence</a>, spiritual purity, and <a href="/wiki/Salvation" title="Salvation">salvation</a> are likewise related to a concept of being in, or returning to, a state of <i>goodness</i>—one that, according to various teachings of "<a href="/wiki/Enlightenment_(concept)" class="mw-redirect" title="Enlightenment (concept)">enlightenment</a>", approaches a state of <i>holiness</i>, righteousness, (or Godliness<i>).</i></p> <h2><span class="mw-headline" id="Good_and_evil">Good and evil</span><span class="mw-editsection"><span class="mw-editsection-bracket">[</span><a href="/w/index.php?title=Good&amp;action=edit&amp;section=8" title="Edit section: Good and evil">edit</a><span class="mw-editsection-bracket">]</span></span></h2> <p>In <a href="/wiki/Religion" title="Religion">religion</a>, <a href="/wiki/Ethics" title="Ethics">ethics</a>, and <a href="/wiki/Philosophy" title="Philosophy">philosophy</a>, <b>"good and evil"</b> is a very common <a href="/wiki/Dichotomy" title="Dichotomy">dichotomy</a>. In cultures with <a href="/wiki/Manichaeism" title="Manichaeism">Manichaean</a> and <a href="/wiki/Abrahamic" class="mw-redirect" title="Abrahamic">Abrahamic</a> religious influence, evil is usually perceived as the antagonistic <a href="/wiki/Dualism" title="Dualism">opposite of good</a>. Good is that which should prevail and evil should be defeated.<sup id="cite_ref-Paul_O._Ingram_1986._P._148-149_13-0" class="reference"><a href="#cite_note-Paul_O._Ingram_1986._P._148-149-13">[13]</a></sup> In cultures with <a href="/wiki/Buddhism" title="Buddhism">Buddhist</a> spiritual influence, this antagonistic duality itself must be overcome through achieving <i><a href="/wiki/%C5%9A%C5%ABnyat%C4%81" title="Śūnyatā">Śūnyatā</a></i>, or emptiness. This is the recognition of good and evil not being unrelated, but two parts of a greater whole; unity, oneness, a <a href="/wiki/Monism" title="Monism">Monism</a>.<sup id="cite_ref-Paul_O._Ingram_1986._P._148-149_13-1" class="reference"><a href="#cite_note-Paul_O._Ingram_1986._P._148-149-13">[13]</a></sup></p> <p>As a religious concept, basic ideas of a <a href="/wiki/Dichotomy" title="Dichotomy">dichotomy</a> between good and evil has developed so that today:</p> <ul> <li><i><a href="/wiki/Good_(religion)" class="mw-redirect" title="Good (religion)">Good</a></i> is a broad concept but it typically deals with an association with <a href="/wiki/Life" title="Life">life</a>, <a href="/wiki/Charity_(virtue)" title="Charity (virtue)">charity</a>, continuity, <a href="/wiki/Happiness" title="Happiness">happiness</a>, <a href="/wiki/Love" title="Love">love</a> and <a href="/wiki/Justice" title="Justice">justice</a>.</li> <li><i><a href="/wiki/Evil" title="Evil">Evil</a></i> is typically associated with conscious and deliberate wrongdoing, discrimination designed to harm others, humiliation of people designed to diminish their psychological needs and dignity, destructiveness, and acts of unnecessary and/or indiscriminate violence.<sup id="cite_ref-14" class="reference"><a href="#cite_note-14">[14]</a></sup></li> <li>the dilemma of the <a href="/wiki/Human_condition" title="Human condition">human condition</a> and humans' and their capacity to perform both good and evil activities.<sup id="cite_ref-15" class="reference"><a href="#cite_note-15">[15]</a></sup></li> </ul> <p>The nature of <i>being good</i> has been given many treatments; one is that the good is based on the natural <a href="/wiki/Love" title="Love">love</a>, bonding, and affection that begins at the earliest stages of personal development; another is that goodness is a product of knowing <a href="/wiki/Truth" title="Truth">truth</a>. Differing views also exist as to why evil might arise. Many religious and philosophical traditions claim that evil behavior is an <i>aberration</i> that results from the imperfect human condition (e.g. "<a href="/wiki/The_Fall_of_Man" class="mw-redirect" title="The Fall of Man">The Fall of Man</a>"). Sometimes, evil is attributed to the existence of <a href="/wiki/Free_will" title="Free will">free will</a> and <a href="/wiki/Agency_(philosophy)" title="Agency (philosophy)">human agency</a>. Some argue that evil itself is ultimately based in an <a href="/wiki/Ignorance" title="Ignorance">ignorance</a> of <a href="/wiki/Truth" title="Truth">truth</a> (i.e., human value, <a href="/wiki/Sanctity" class="mw-redirect" title="Sanctity">sanctity</a>, <a href="/wiki/Divinity" title="Divinity">divinity</a>). A variety of Enlightenment thinkers have alleged the opposite, by suggesting that evil is learned as a consequence of tyrannical <a href="/wiki/Social_structure" title="Social structure">social structures</a>.</p> <p>In <a href="/wiki/Religion" title="Religion">religion</a>, <a href="/wiki/Ethics" title="Ethics">ethics</a>, and <a href="/wiki/Philosophy" title="Philosophy">philosophy</a>, <b>goodness and evil</b>, or simply <b>good and evil</b>, is the concept of all human <a href="/wiki/Desire" title="Desire">desires</a> and <a href="/wiki/Behavior" title="Behavior">behaviors</a> as conforming to a <i><a href="/wiki/Dualist" class="mw-redirect" title="Dualist">dualistic</a> <a href="/wiki/Spectrum" title="Spectrum">spectrum</a></i>—wherein in one direction are aspects that are <i><a href="/wiki/Wisdom" title="Wisdom">wisely</a> reverent of <a href="/wiki/Life" title="Life">life</a> and continuity</i> ("<a href="/wiki/Good_(religion)" class="mw-redirect" title="Good (religion)">good</a>"), and in the other are aspects that are <i><a href="/wiki/Vanity" title="Vanity">vainly</a> reverent of <a href="/wiki/Death" title="Death">death</a> and destruction</i> ("<a href="/wiki/Evil" title="Evil">evil</a>").</p> <p>Religious and philosophical views tend to agree that, while "good and evil" is <i>a <a href="/wiki/Concept" title="Concept">concept</a></i> and therefore <i>an <a href="/wiki/Abstraction" title="Abstraction">abstraction</a></i>, <b>goodness</b> is intrinsic to human <a href="/wiki/Nature" title="Nature">nature</a> and is ultimately based on the natural <a href="/wiki/Love" title="Love">love</a>, bonding, affection that people grow to feel for other people.</p> <p>Likewise, most religious and philosophical interpretations agree that <b>evil</b> is ultimately based in an <a href="/wiki/Ignorance" title="Ignorance">ignorance</a> of <a href="/wiki/Truth" title="Truth">truth</a> (i.e. human value, <a href="/wiki/Sanctity" class="mw-redirect" title="Sanctity">sanctity</a>, <a href="/wiki/Divinity" title="Divinity">divinity</a>), and evil behavior itself is an <i>aberration</i>&#160;—one that defies any <a href="/wiki/Understanding" title="Understanding">understanding</a> save that the path to evil is one of confusion and excessive desire (<a href="/wiki/Greed" title="Greed">greed</a>). In <a href="/wiki/Physics" title="Physics">physics</a> and <a href="/wiki/Statistical_thermodynamics" class="mw-redirect" title="Statistical thermodynamics">statistical thermodynamics</a>, the property of goodness or order is often referred to as a state of low <a href="/wiki/Entropy" title="Entropy">entropy</a>.</p> <p>The two questions are subtly different. One may answer the first question by researching the world by use of social science, and examining the preferences that people assert. However, one may answer the second question by use of reasoning, introspection, prescription, and generalization. The former kind of method of analysis is called "<a href="/wiki/Descriptive" class="mw-redirect" title="Descriptive">descriptive</a>", because it attempts to describe what people actually view as good or evil; while the latter is called "<a href="/wiki/Norm_(philosophy)" title="Norm (philosophy)">normative</a>", because it tries to actively prohibit evils and cherish goods. These descriptive and normative approaches can be complementary. For example, tracking the decline of the popularity of slavery across cultures is the work of descriptive ethics, while advising that slavery be avoided is normative.</p> <p><a href="/wiki/Meta-ethics" title="Meta-ethics">Meta-ethics</a> is the study of the fundamental questions concerning the nature and origins of the good and the evil, including inquiry into the nature of good and evil, as well as the meaning of evaluative language. In this respect, meta-ethics is not necessarily tied to investigations into how others see the good, or of asserting what is good.</p> <h2><span id="Descriptive.2C_meta-ethical.2C_and_normative_fields"></span><span class="mw-headline" id="Descriptive,_meta-ethical,_and_normative_fields">Descriptive, meta-ethical, and normative fields</span><span class="mw-editsection"><span class="mw-editsection-bracket">[</span><a href="/w/index.php?title=Good&amp;action=edit&amp;section=9" title="Edit section: Descriptive, meta-ethical, and normative fields">edit</a><span class="mw-editsection-bracket">]</span></span></h2> <table class="plainlinks metadata ambox ambox-content ambox-Unreferenced" role="presentation"> <tr> <td class="mbox-image"> <div style="width:52px"><a href="/wiki/File:Question_book-new.svg" class="image"><img alt="" src="//upload.wikimedia.org/wikipedia/en/thumb/9/99/Question_book-new.svg/50px-Question_book-new.svg.png" width="50" height="39" srcset="//upload.wikimedia.org/wikipedia/en/thumb/9/99/Question_book-new.svg/75px-Question_book-new.svg.png 1.5x, //upload.wikimedia.org/wikipedia/en/thumb/9/99/Question_book-new.svg/100px-Question_book-new.svg.png 2x" data-file-width="512" data-file-height="399" /></a></div> </td> <td class="mbox-text"> <div class="mbox-text-span">This section <b>does not <a href="/wiki/Wikipedia:Citing_sources" title="Wikipedia:Citing sources">cite</a> any <a href="/wiki/Wikipedia:Verifiability" title="Wikipedia:Verifiability">sources</a></b>. <span class="hide-when-compact">Please help <a class="external text" href="//en.wikipedia.org/w/index.php?title=Good&amp;action=edit">improve this section</a> by <a href="/wiki/Help:Introduction_to_referencing_with_Wiki_Markup/1" title="Help:Introduction to referencing with Wiki Markup/1">adding citations to reliable sources</a>. Unsourced material may be challenged and <a href="/wiki/Wikipedia:Verifiability#Burden_of_evidence" title="Wikipedia:Verifiability">removed</a>.</span> <small><i>(November 2012)</i></small> <small class="hide-when-compact"><i>(<a href="/wiki/Help:Maintenance_template_removal" title="Help:Maintenance template removal">Learn how and when to remove this template message</a>)</i></small></div> </td> </tr> </table> <p>It is possible to treat the essential theories of value by the use of a philosophical and academic approach. In properly analyzing theories of value, everyday beliefs are not only carefully <i>catalogued</i> and <i>described</i>, but also rigorously <i>analyzed</i> and <i>judged</i>.</p> <p>There are at least two basic ways of presenting a theory of value, based on two different kinds of questions:</p> <ul> <li>What do people find good, and what do they despise?</li> <li>What really is good, and what really is bad?</li> </ul> <h2><span class="mw-headline" id="Theories_of_the_intrinsically_good">Theories of the intrinsically good</span><span class="mw-editsection"><span class="mw-editsection-bracket">[</span><a href="/w/index.php?title=Good&amp;action=edit&amp;section=10" title="Edit section: Theories of the intrinsically good">edit</a><span class="mw-editsection-bracket">]</span></span></h2> <table class="plainlinks metadata ambox ambox-content ambox-Unreferenced" role="presentation"> <tr> <td class="mbox-image"> <div style="width:52px"><a href="/wiki/File:Question_book-new.svg" class="image"><img alt="" src="//upload.wikimedia.org/wikipedia/en/thumb/9/99/Question_book-new.svg/50px-Question_book-new.svg.png" width="50" height="39" srcset="//upload.wikimedia.org/wikipedia/en/thumb/9/99/Question_book-new.svg/75px-Question_book-new.svg.png 1.5x, //upload.wikimedia.org/wikipedia/en/thumb/9/99/Question_book-new.svg/100px-Question_book-new.svg.png 2x" data-file-width="512" data-file-height="399" /></a></div> </td> <td class="mbox-text"> <div class="mbox-text-span">This section <b>does not <a href="/wiki/Wikipedia:Citing_sources" title="Wikipedia:Citing sources">cite</a> any <a href="/wiki/Wikipedia:Verifiability" title="Wikipedia:Verifiability">sources</a></b>. <span class="hide-when-compact">Please help <a class="external text" href="//en.wikipedia.org/w/index.php?title=Good&amp;action=edit">improve this section</a> by <a href="/wiki/Help:Introduction_to_referencing_with_Wiki_Markup/1" title="Help:Introduction to referencing with Wiki Markup/1">adding citations to reliable sources</a>. Unsourced material may be challenged and <a href="/wiki/Wikipedia:Verifiability#Burden_of_evidence" title="Wikipedia:Verifiability">removed</a>.</span> <small><i>(April 2010)</i></small> <small class="hide-when-compact"><i>(<a href="/wiki/Help:Maintenance_template_removal" title="Help:Maintenance template removal">Learn how and when to remove this template message</a>)</i></small></div> </td> </tr> </table> <p>A satisfying formulation of goodness is valuable because it might allow one to construct a good life or society by reliable processes of deduction, elaboration, or prioritization. One could answer the ancient question, "How should we then live?" among many other important related questions. It has long been thought that this question can best be answered by examining what it is that necessarily makes a thing valuable, or in what the source of value consists.</p> <h3><span class="mw-headline" id="Transcendental_realism">Transcendental realism</span><span class="mw-editsection"><span class="mw-editsection-bracket">[</span><a href="/w/index.php?title=Good&amp;action=edit&amp;section=11" title="Edit section: Transcendental realism">edit</a><span class="mw-editsection-bracket">]</span></span></h3> <p>One attempt to define goodness describes it as a property of the world with <a href="/wiki/Transcendental_realism" title="Transcendental realism">transcendental realism</a>. According to this claim, to talk about the good is to talk about something real that exists in the object itself, independent of the perception of it. <a href="/wiki/Plato" title="Plato">Plato</a> advocated this view, in his expression that there is such a thing as an eternal realm of <a href="/wiki/Substantial_form" title="Substantial form">forms</a> or ideas, and that the greatest of the ideas and the essence of being was goodness, or The good. The good was defined by many ancient Greeks and other ancient philosophers as a perfect and eternal idea, or blueprint. The good is the right relation between all that exists, and this exists in the mind of the Divine, or some heavenly realm. The good is the <a href="/wiki/Harmony" title="Harmony">harmony</a> of a just political community, love, friendship, the ordered human soul of <a href="/wiki/Virtues" class="mw-redirect" title="Virtues">virtues</a>, and the right relation to the Divine and to Nature. The characters in Plato's dialogues mention the many virtues of a philosopher, or a lover of wisdom.</p> <p>A <a href="/wiki/Theism" title="Theism">theist</a> is a person who believes that <a href="/wiki/Deity" title="Deity">gods</a> exist (<a href="/wiki/Monotheism" title="Monotheism">monotheism</a> or <a href="/wiki/Polytheism" title="Polytheism">polytheism</a>). A theist may, therefore, claim that the universe has a purpose and value according to the will of such <a href="/wiki/Creator_deity" title="Creator deity">creator</a>(s) that lies partially beyond human understanding. For instance, <a href="/wiki/Thomas_Aquinas" title="Thomas Aquinas">Thomas Aquinas</a>—a proponent of this view—believed he had proven the <a href="/wiki/Existence_of_God" title="Existence of God">existence of God</a>, and the right relations that humans ought to have to the divine <a href="/wiki/First_cause" class="mw-redirect" title="First cause">first cause</a>.</p> <p>Monotheists might also hope for infinite universal love. Such hope is often translated as "<a href="/wiki/Faith" title="Faith">faith</a>", and <a href="/wiki/Wisdom" title="Wisdom">wisdom</a> itself is largely defined within some religious doctrines as <i>a knowledge and understanding of innate goodness.</i> The concepts of <a href="/wiki/Innocence" title="Innocence">innocence</a>, <a href="/wiki/Spirituality" title="Spirituality">spiritual</a> <a href="https://en.wiktionary.org/wiki/Purity" class="extiw" title="wikt:Purity">purity</a>, and <a href="/wiki/Salvation" title="Salvation">salvation</a> are likewise related to a concept of being in, or returning to, a state of <i>goodness</i>—one that, according to various teachings of "<a href="/wiki/Enlightenment_(spiritual)" title="Enlightenment (spiritual)">enlightenment</a>", approaches a state of <i>holiness</i> (or Godliness<i>).</i>\</p> <h3><span class="mw-headline" id="Perfectionism">Perfectionism</span><span class="mw-editsection"><span class="mw-editsection-bracket">[</span><a href="/w/index.php?title=Good&amp;action=edit&amp;section=12" title="Edit section: Perfectionism">edit</a><span class="mw-editsection-bracket">]</span></span></h3> <p><a href="/wiki/Aristotle" title="Aristotle">Aristotle</a> believed that virtues consisted of realization of potentials unique to humanity, such as the use of reason. This type of view, called <a href="/wiki/Perfectionism_(philosophy)" title="Perfectionism (philosophy)">perfectionism</a>, has been recently defended in modern form by <a href="/wiki/Thomas_Hurka" title="Thomas Hurka">Thomas Hurka</a>.</p> <p>An entirely different form of perfectionism has arisen in response to rapid technological change. Some <a href="/w/index.php?title=Techno-optimist&amp;action=edit&amp;redlink=1" class="new" title="Techno-optimist (page does not exist)">techno-optimists</a>, especially <a href="/wiki/Transhumanism" title="Transhumanism">transhumanists</a>, avow a form of perfectionism in which the capacity to determine good and trade off fundamental values, is expressed not by humans but by software, <a href="/wiki/Genetic_engineering" title="Genetic engineering">genetic engineering</a> of humans, <a href="/wiki/Artificial_intelligence" title="Artificial intelligence">artificial intelligence</a>. Skeptics assert that rather than perfect goodness, it would be only the appearance of perfect goodness, reinforced by <a href="/wiki/Persuasion_technology" class="mw-redirect" title="Persuasion technology">persuasion technology</a> and probably brute force of violent <a href="/wiki/Technological_escalation" title="Technological escalation">technological escalation</a>, which would cause people to accept such rulers or rules authored by them.</p> <h4><span class="mw-headline" id="Subjective_theories_of_well-being">Subjective theories of well-being</span><span class="mw-editsection"><span class="mw-editsection-bracket">[</span><a href="/w/index.php?title=Good&amp;action=edit&amp;section=13" title="Edit section: Subjective theories of well-being">edit</a><span class="mw-editsection-bracket">]</span></span></h4> <p>It is difficult to figure out where an immaterial trait such as "goodness" could reside in the world. A counterproposal is to locate values inside people. Some philosophers go so far as to say that if some <a href="/wiki/State_of_affairs_(philosophy)" title="State of affairs (philosophy)">state of affairs</a> does not tend to arouse a desirable subjective state in self-aware beings, then it cannot be good.</p> <p>Most philosophers that think goods have to create desirable mental states also say that goods are experiences of self-aware beings. These philosophers often distinguish the experience, which they call an intrinsic good, from the things that seem to cause the experience, which they call "inherent" goods. Failing to distinguish the two leads to a <a href="/w/index.php?title=Subject-object_problem&amp;action=edit&amp;redlink=1" class="new" title="Subject-object problem (page does not exist)">subject-object problem</a> in which it is not clear who is evaluating what object.</p> <p>Some theories describe no higher collective value than that of maximizing <i>pleasure</i> for individual(s). Some even define goodness and intrinsic value as the experience of pleasure, and bad as the experience of pain. This view is called <a href="/wiki/Hedonism" title="Hedonism">hedonism</a>, a <i>monistic theory of value</i>. It has two main varieties: simple, and Epicurean.</p> <p><i>Simple hedonism</i> is the view that physical pleasure is the ultimate good. However, the ancient philosopher <a href="/wiki/Epicurus" title="Epicurus">Epicurus</a> used the word 'pleasure' in a more general sense that encompassed a range of states from bliss to contentment to relief. Contrary to popular caricature, he valued pleasures of the mind to bodily pleasures, and advocated moderation as the surest path to happiness.</p> <p><a href="/wiki/Jeremy_Bentham" title="Jeremy Bentham">Jeremy Bentham's</a> book <i><a href="/w/index.php?title=The_Principles_of_Morals_and_Legislation&amp;action=edit&amp;redlink=1" class="new" title="The Principles of Morals and Legislation (page does not exist)">The Principles of Morals and Legislation</a></i> prioritized goods by considering pleasure, pain and consequences. This theory had a wide effect on public affairs, up to and including the present day. A similar system was later named <a href="/wiki/Utilitarianism" title="Utilitarianism">Utilitarianism</a> by <a href="/wiki/John_Stuart_Mill" title="John Stuart Mill">John Stuart Mill</a>. More broadly, utilitarian theories are examples of Consequentialism. All utilitarian theories are based upon the <i>maxim of utility</i>, which states that <i>good</i> is whatever provides <i>the greatest happiness for the greatest number</i>. It follows from this principle that what brings happiness to the greatest number of people, is good.</p> <p>A benefit of tracing good to pleasure and pain is that both are easily understandable, both in oneself and to an extent in others. For the hedonist, the explanation for helping behaviour may come in the form of <i>empathy</i>—the ability of a being to "feel" another's pain. People tend to value the lives of gorillas more than those of mosquitoes because the gorilla lives and feels, making it easier to empathize with them. This idea is carried forward in the <a href="/wiki/Ethical_relationship" title="Ethical relationship">ethical relationship</a> view and has given rise to the <a href="/wiki/Animal_rights" title="Animal rights">animal rights</a> movement and parts of the <a href="/wiki/Peace_movement" title="Peace movement">peace movement</a>. The impact of sympathy on human behaviour is compatible with <a href="/wiki/The_Enlightenment" class="mw-redirect" title="The Enlightenment">Enlightenment</a> views, including <a href="/wiki/David_Hume" title="David Hume">David Hume's</a> stances that the idea of a self with unique identity is illusory, and that morality ultimately comes down to sympathy and fellow feeling for others, or the exercise of approval underlying moral judgments.</p> <p>A view adopted by James Griffin attempts to find a subjective alternative to hedonism as an intrinsic value. He argues that the satisfaction of one's informed desires constitutes well-being, whether or not these desires actually bring the agent happiness. Moreover, these preferences must be life-relevant, that is, contribute to the success of a person's life overall.</p> <p>Desire satisfaction may occur without the agent's awareness of the satisfaction of the desire. For example, if a man wishes for his legal will to be enacted after his death, and it is, then his desire has been satisfied even though he will never experience or know of it.</p> <p><a href="/wiki/Meher_Baba" title="Meher Baba">Meher Baba</a> proposed that it is not the satisfaction of desires that motivates the agent but rather "a desire to be free from the limitation of all desires. Those experiences and actions which increase the fetters of desire are bad, and those experiences and actions which tend to emancipate the mind from limiting desires are good."<sup id="cite_ref-16" class="reference"><a href="#cite_note-16">[16]</a></sup> It is through good actions, then, that the agent becomes free from selfish desires and achieves a state of well-being: "The good is the main link between selfishness thriving and dying. Selfishness, which in the beginning is the father of evil tendencies, becomes through good deeds the hero of its own defeat. When the evil tendencies are completely replaced by good tendencies, selfishness is transformed into selflessness, i.e., individual selfishness loses itself in universal interest."<sup id="cite_ref-17" class="reference"><a href="#cite_note-17">[17]</a></sup></p> <h4><span class="mw-headline" id="Objective_theories_of_well-being">Objective theories of well-being</span><span class="mw-editsection"><span class="mw-editsection-bracket">[</span><a href="/w/index.php?title=Good&amp;action=edit&amp;section=14" title="Edit section: Objective theories of well-being">edit</a><span class="mw-editsection-bracket">]</span></span></h4> <dl> <dd><i>See the separate analysis of <a href="/wiki/Wealth" title="Wealth">wealth</a>.</i></dd> </dl> <p>The idea that the ultimate good exists and is not orderable but is globally measurable is reflected in various ways in economic (<a href="/wiki/Classical_economics" title="Classical economics">classical economics</a>, <a href="/wiki/Green_economics" class="mw-redirect" title="Green economics">green economics</a>, <a href="/wiki/Welfare_economics" title="Welfare economics">welfare economics</a>, <a href="/wiki/Gross_national_happiness" class="mw-redirect" title="Gross national happiness">gross national happiness</a>) and scientific (<a href="/wiki/Positive_psychology" title="Positive psychology">positive psychology</a>, the <a href="/wiki/Science_of_morality" title="Science of morality">science of morality</a>) <a href="/wiki/Measuring_well-being" class="mw-redirect" title="Measuring well-being">well-being measuring</a> theories, all of which focus on various ways of assessing progress towards that goal, a so-called <a href="/wiki/Genuine_progress_indicator" title="Genuine progress indicator">genuine progress indicator</a>. Modern economics thus reflects very ancient philosophy, but a calculation or quantitative or other process based on cardinality and <a href="/wiki/Statistics" title="Statistics">statistics</a> replaces the simple ordering of values.</p> <p>For example, in both economics and in folk wisdom, the value of something seems to rise so long as it is relatively scarce. However, if it becomes too scarce, it leads often to a conflict, and can reduce collective value.</p> <p>In the classical <a href="/wiki/Political_economy" title="Political economy">political economy</a> of <a href="/wiki/Adam_Smith" title="Adam Smith">Adam Smith</a> and <a href="/wiki/David_Ricardo" title="David Ricardo">David Ricardo</a>, and in its critique by <a href="/wiki/Karl_Marx" title="Karl Marx">Karl Marx</a>, <i>human labour</i> is seen as the ultimate source of all new economic value. This is an <i>objective</i> theory of value (see <a href="/wiki/Value_theory" title="Value theory">value theory</a>), which attributes value to real production-costs, and ultimately expenditures of human labour-time (see also <a href="/wiki/Law_of_value" title="Law of value">law of value</a>). It contrasts with <a href="/wiki/Marginal_utility" title="Marginal utility">marginal utility</a> theory, which argues that the value of labour depends on subjective preferences by consumers, which may however also be objectively studied.</p> <p>The economic value of labour may be assessed technically in terms of its <a href="/wiki/Use-value" class="mw-redirect" title="Use-value">use-value</a> or <a href="/wiki/Utility" title="Utility">utility</a> or commercially in terms of its <a href="/wiki/Exchange-value" class="mw-redirect" title="Exchange-value">exchange-value</a>, <a href="/wiki/Price" title="Price">price</a> or production cost (see also <a href="/wiki/Labour_power" title="Labour power">labour power</a>. But its value may also be socially assessed in terms of its contribution to the <a href="/wiki/Wealth" title="Wealth">wealth</a> and <a href="/wiki/Well-being" title="Well-being">well-being</a> of a society.</p> <p>In non-market societies, labour may be valued primarily in terms of skill, time, and output, as well as moral or social criteria and legal obligations. In market societies, labour is valued economically primarily through the <a href="/wiki/Labour_market" class="mw-redirect" title="Labour market">labour market</a>. The price of labour may then be set by supply and demand, by <a href="/wiki/Strike_action" title="Strike action">strike action</a> or legislation, or by legal or professional entry-requirements into occupations.</p> <h4><span class="mw-headline" id="Mid-range_theories">Mid-range theories</span><span class="mw-editsection"><span class="mw-editsection-bracket">[</span><a href="/w/index.php?title=Good&amp;action=edit&amp;section=15" title="Edit section: Mid-range theories">edit</a><span class="mw-editsection-bracket">]</span></span></h4> <p><a href="/wiki/Conceptual_metaphor" title="Conceptual metaphor">Conceptual metaphor</a> theories argue against both subjective and <a href="/wiki/Objectivity_(philosophy)" title="Objectivity (philosophy)">objective</a> conceptions of value and meaning, and focus on the relationships between body and other essential elements of human life. In effect, conceptual metaphor theories treat ethics as an <a href="/wiki/Ontology" title="Ontology">ontology</a> problem and the issue of how to work-out values as a negotiation of these metaphors, not the application of some <a href="/wiki/Abstraction" title="Abstraction">abstraction</a> or a strict standoff between parties who have no way to understand each other's views.</p> <h2><span class="mw-headline" id="Goodness_and_agency">Goodness and agency</span><span class="mw-editsection"><span class="mw-editsection-bracket">[</span><a href="/w/index.php?title=Good&amp;action=edit&amp;section=16" title="Edit section: Goodness and agency">edit</a><span class="mw-editsection-bracket">]</span></span></h2> <h3><span class="mw-headline" id="Goodwill">Goodwill</span><span class="mw-editsection"><span class="mw-editsection-bracket">[</span><a href="/w/index.php?title=Good&amp;action=edit&amp;section=17" title="Edit section: Goodwill">edit</a><span class="mw-editsection-bracket">]</span></span></h3> <p><a href="/wiki/John_Rawls" title="John Rawls">John Rawls</a>' book <i><a href="/wiki/A_Theory_of_Justice" title="A Theory of Justice">A Theory of Justice</a></i> prioritized social arrangements and goods based on their contribution to <a href="/wiki/Justice" title="Justice">justice</a>. Rawls defined justice as <i>fairness</i>, especially in distributing social goods, defined fairness in terms of procedures, and attempted to prove that just institutions and lives are good, if rational individuals' goods are considered fairly. Rawls's crucial invention was the <a href="/wiki/Original_position" title="Original position">original position</a>, a procedure in which one tries to make objective moral decisions by refusing to let personal facts about oneself enter one's moral calculations.</p> <p>One problem with the thinkings of Rawls is that it is overly procedural as found in defenders of <a href="/wiki/Natural_law" title="Natural law">natural law</a>. Procedurally fair processes of the type used by Rawls may not leave enough room for judgment, and therefore, reduce the totality of goodness. For example, if two people are found to own an orange, the standard fair procedure is to cut it in two and give half to each. However, if one wants to eat it while the other wants the rind to flavor a cake, cutting it in two is clearly less good than giving the peel to the baker and feeding the core to the eater.</p> <p>Applying procedural fairness to an entire society therefore seems certain to create recognizable inefficiencies<sup class="noprint Inline-Template Template-Fact" style="white-space:nowrap;">[<i><a href="/wiki/Wikipedia:Citation_needed" title="Wikipedia:Citation needed"><span title="This claim needs references to reliable sources. (August 2009)">citation needed</span></a></i>]</sup>, and therefore be unfair, and (by the equivalence of justice with fairness) unjust.</p> <p>However, procedural processes are not always necessarily damning in this way. <a href="/wiki/Immanuel_Kant" title="Immanuel Kant">Immanuel Kant</a>, a great influence for Rawls, similarly applies a lot of procedural practice within the practical application of <i>The Categorical Imperative</i>, however, this is indeed not based solely on 'fairness'. Even though an example like the one above regarding the orange would not be something that required the practical application of <i>The Categorical Imperative</i>, it is important <sup class="noprint Inline-Template" style="margin-left:0.1em; white-space:nowrap;">[<i><a href="/wiki/Wikipedia:Manual_of_Style/Words_to_watch#Unsupported_attributions" title="Wikipedia:Manual of Style/Words to watch"><span title="The material near this tag may use weasel words or too-vague attribution. (August 2009)">according to whom?</span></a></i>]</sup> to draw distinction between Kant and Rawls, and note that Kant's Theory would not necessarily lead to the same problems Rawls' does&#160;— i.e., the cutting in half of the orange. Kant's Theory promotes acting out of Duty&#160;— acting for the Summum Bonum for him, <i>The Good Will</i> - and in fact encourages Judgement, too. What this would mean <sup class="noprint Inline-Template" style="margin-left:0.1em; white-space:nowrap;">[<i><a href="/wiki/Wikipedia:Manual_of_Style/Words_to_watch#Unsupported_attributions" title="Wikipedia:Manual of Style/Words to watch"><span title="The material near this tag may use weasel words or too-vague attribution. (August 2009)">according to whom?</span></a></i>]</sup> is that the outcome of the Orange's distribution would not be such a simple process for Kant as the reason why it would be wanted by both parties would necessarily have to be a part of the Judgement process, thus eliminating the problem that Rawls' account suffers here.</p> <h3><span id="Society.2C_life_and_ecology"></span><span class="mw-headline" id="Society,_life_and_ecology">Society, life and ecology</span><span class="mw-editsection"><span class="mw-editsection-bracket">[</span><a href="/w/index.php?title=Good&amp;action=edit&amp;section=18" title="Edit section: Society, life and ecology">edit</a><span class="mw-editsection-bracket">]</span></span></h3> <table class="plainlinks metadata ambox ambox-style ambox-essay-like" role="presentation"> <tr> <td class="mbox-image"> <div style="width:52px"><img alt="" src="//upload.wikimedia.org/wikipedia/en/thumb/f/f2/Edit-clear.svg/40px-Edit-clear.svg.png" width="40" height="40" srcset="//upload.wikimedia.org/wikipedia/en/thumb/f/f2/Edit-clear.svg/60px-Edit-clear.svg.png 1.5x, //upload.wikimedia.org/wikipedia/en/thumb/f/f2/Edit-clear.svg/80px-Edit-clear.svg.png 2x" data-file-width="48" data-file-height="48" /></div> </td> <td class="mbox-text"> <div class="mbox-text-span">This section <b>is written like a&#160;personal reflection or opinion essay</b> that states a Wikipedia editor's personal feelings about a topic. <span class="hide-when-compact">Please <a class="external text" href="//en.wikipedia.org/w/index.php?title=Good&amp;action=edit">help improve it</a> by rewriting it in an <a href="/wiki/Wikipedia:Encyclopedic_style" class="mw-redirect" title="Wikipedia:Encyclopedic style">encyclopedic style</a>.</span> <small><i>(September 2012)</i></small> <small class="hide-when-compact"><i>(<a href="/wiki/Help:Maintenance_template_removal" title="Help:Maintenance template removal">Learn how and when to remove this template message</a>)</i></small></div> </td> </tr> </table> <p>Many views value <i>unity</i> as a good: to go beyond <a href="/wiki/Eudaimonia" title="Eudaimonia">eudaimonia</a> by saying that an individual person's flourishing is valuable only as a means to the flourishing of society as a whole. In other words, a single person's life is, ultimately, not important or worthwhile in itself, but is good only as a means to the success of society as a whole. Some elements of <a href="/wiki/Confucianism" title="Confucianism">Confucianism</a> are an example of this, encouraging the view that people ought to conform as individuals to demands of a peaceful and ordered society.</p> <p>According to the naturalistic view, the flourishing of society is not, or not the only, intrinsically good thing. Defenses of this notion are often formulated by reference to <a href="/wiki/Biology" title="Biology">biology</a>, and observations that living things compete more with their own kind than with other kinds. Rather, what is of intrinsic good is the flourishing of all sentient life, extending to those animals that have some level of similar sentience, such as <a href="/wiki/Great_Ape_personhood" class="mw-redirect" title="Great Ape personhood">Great Ape personhood</a>. Others go farther, declaring that life itself is of intrinsic value.</p> <p>By another approach, one achieves peace and agreement by focusing, not on one's peers (who may be rivals or competitors), but on the common environment. The reasoning: As living beings it is clearly and objectively good that we are surrounded by an <a href="/wiki/Ecosystem" title="Ecosystem">ecosystem</a> that supports <a href="/wiki/Life" title="Life">life</a>. Indeed, if we weren't, we could neither discuss that good nor even recognize it. The <a href="/wiki/Anthropic_principle" title="Anthropic principle">anthropic principle</a> in <a href="/wiki/Physical_cosmology" title="Physical cosmology">cosmology</a> recognizes this view.<sup class="noprint Inline-Template Template-Fact" style="white-space:nowrap;">[<i><a href="/wiki/Wikipedia:Citation_needed" title="Wikipedia:Citation needed"><span title="This claim needs references to reliable sources. (May 2009)">citation needed</span></a></i>]</sup></p> <p>Under <a href="/wiki/Materialism" title="Materialism">materialism</a> or even <a href="/wiki/Embodied_cognition" title="Embodied cognition">embodiment</a> values, or in any system that recognizes the validity of <a href="/wiki/Ecology" title="Ecology">ecology</a> as a scientific study of limits and potentials, an ecosystem is a fundamental good. To all who investigate, it seems that goodness, or value, exists within an ecosystem, <a href="/wiki/Earth" title="Earth">Earth</a>. Creatures within that ecosystem and wholly dependent on it, evaluate good relative to what else could be achieved there. In other words, good is <a href="/wiki/Situated" title="Situated">situated</a> in a particular place and one does not dismiss everything that is not available there (such as very low gravity or absolutely abundant sugar candy) as "not good enough", one works within its constraints. Transcending them and learning to be satisfied with them, is thus another sort of value, perhaps called <a href="/wiki/Contentment" title="Contentment">satisfaction</a>.</p> <p>Values and the people that hold them seem necessarily subordinate to the ecosystem. If this is so, then what kind of being could validly apply the word "good" to an ecosystem as a whole? Who would have the power to assess and judge an ecosystem as good or bad? By what criteria? And by what criteria would ecosystems be modified, especially larger ones such as the <a href="/wiki/Atmosphere" title="Atmosphere">atmosphere</a> (<a href="/wiki/Climate_change" title="Climate change">climate change</a>) or <a href="/wiki/Ocean" title="Ocean">oceans</a> (<a href="/wiki/Extinction" title="Extinction">extinction</a>) or <a href="/wiki/Forests" class="mw-redirect" title="Forests">forests</a> (<a href="/wiki/Deforestation" title="Deforestation">deforestation</a>)?<sup id="cite_ref-18" class="reference"><a href="#cite_note-18">[18]</a></sup></p> <p>"Remaining on Earth" as the most basic value. While <a href="/wiki/Green_politics" title="Green politics">green</a> <a href="/wiki/List_of_ethicists" title="List of ethicists">ethicists</a> have been most forthright about it, and have developed theories of <a href="/wiki/Gaia_philosophy" title="Gaia philosophy">Gaia philosophy</a>, <a href="/wiki/Biophilia_hypothesis" title="Biophilia hypothesis">biophilia</a>, <a href="/wiki/Bioregionalism" title="Bioregionalism">bioregionalism</a> that reflect it, the questions are now universally recognized as central in determining value, e.g. the economic "<a href="/wiki/Value_of_Earth" title="Value of Earth">value of Earth</a>" to humans as a whole, or the "<a href="/wiki/Value_of_life" title="Value of life">value of life</a>" that is neither whole-Earth nor human. Many have come to the conclusion that without assuming ecosystem continuation as a universal good, with attendant <a href="/wiki/Virtue" title="Virtue">virtues</a> like <a href="/wiki/Biodiversity" title="Biodiversity">biodiversity</a> and <a href="/wiki/Ecological_wisdom" class="mw-redirect" title="Ecological wisdom">ecological wisdom</a> it is impossible to justify such operational requirements as <a href="/wiki/Sustainability" title="Sustainability">sustainability</a> of human activity on Earth.</p> <p>One response is that humans are not necessarily confined to Earth, and could use it and move on. A counter-argument is that only a tiny fraction of humans could do this—and they would be self-selected by ability to do <a href="/wiki/Technological_escalation" title="Technological escalation">technological escalation</a> on others (for instance, the ability to create large spacecraft to flee the planet in, and simultaneously fend off others who seek to prevent them). Another counter-argument is that <a href="/wiki/Extraterrestrial_life" title="Extraterrestrial life">extraterrestrial life</a> would encounter the fleeing humans and destroy them as a <a href="/wiki/Locust" title="Locust">locust</a> species. A third is that if there are no other worlds fit to support life (and no <a href="/wiki/Extraterrestrials" class="mw-redirect" title="Extraterrestrials">extraterrestrials</a> who compete with <a href="/wiki/Humans" class="mw-redirect" title="Humans">humans</a> to occupy them) it is both futile to flee, and foolish to imagine that it would take less energy and skill to protect the Earth as a <a href="/wiki/Habitat_(ecology)" class="mw-redirect" title="Habitat (ecology)">habitat</a> than it would take to construct some new habitat.</p> <p>Accordingly, remaining on Earth, as a living being surrounded by a working ecosystem, is a fair statement of the most basic values and goodness to any being we are able to communicate with. A moral system without this axiom seems simply not actionable.</p> <p>However, most religious systems acknowledge an <a href="/wiki/Afterlife" title="Afterlife">afterlife</a> and improving this is seen as an even more basic good. In many other moral systems, also, remaining on Earth in a state that lacks <a href="/wiki/Honour" title="Honour">honor</a> or power over self is less desirable&#160;— consider <a href="/wiki/Seppuku" title="Seppuku">seppuku</a> in <a href="/wiki/Bushido" title="Bushido">bushido</a>, <a href="/wiki/Kamikaze" title="Kamikaze">kamikazes</a> or the role of <a href="/wiki/Suicide_attack" title="Suicide attack">suicide attacks</a> in <a href="/wiki/Jihadi" class="mw-redirect" title="Jihadi">Jihadi</a> rhetoric. In all these systems, remaining on Earth is perhaps no higher than a third-place value.</p> <p>Radical values <a href="/wiki/Environmentalism" title="Environmentalism">environmentalism</a> can be seen as either a very old or a very new view: that the only intrinsically good thing is a flourishing ecosystem; individuals and societies are merely instrumentally valuable, good only as means to having a flourishing ecosystem. The <a href="/wiki/Gaia_philosophy" title="Gaia philosophy">Gaia philosophy</a> is the most detailed expression of this overall thought but it strongly influenced <a href="/wiki/Deep_Ecology" class="mw-redirect" title="Deep Ecology">Deep Ecology</a> and the modern <a href="/wiki/Green_Parties" class="mw-redirect" title="Green Parties">Green Parties</a>.</p> <p>It is often claimed that <a href="/wiki/Indigenous_peoples" title="Indigenous peoples">aboriginal</a> peoples never lost this sort of view. <a href="/wiki/Anthropological_linguistics" title="Anthropological linguistics">Anthropological linguistics</a> studies links between their languages and the ecosystems they lived in, which gave rise to their <a href="/wiki/Knowledge" title="Knowledge">knowledge</a> distinctions. Very often, environmental cognition and <a href="/wiki/Moral" title="Moral">moral</a> cognition were not distinguished in these languages. Offenses to nature were like those to other people, and <a href="/wiki/Animism" title="Animism">Animism</a> reinforced this by giving nature "personality" via <a href="/wiki/Mythology" title="Mythology">myth</a>. <a href="/wiki/Anthropological_theories_of_value" title="Anthropological theories of value">Anthropological theories of value</a> explore these questions.</p> <p>Most people in the world reject older <a href="/wiki/Situated_ethics" title="Situated ethics">situated ethics</a> and localized religious views. However small-community-based and ecology-centric views have gained some popularity in recent years. In part, this has been attributed to the desire for ethical certainties. Such a deeply rooted definition of goodness would be valuable because it might allow one to construct a good life or society by reliable processes of deduction, elaboration or prioritisation. Ones that relied only on local referents one could verify for oneself, creating more certainty and therefore less investment in protection, hedging and insuring against consequences of loss of the value.</p> <h3><span class="mw-headline" id="History_and_novelty">History and novelty</span><span class="mw-editsection"><span class="mw-editsection-bracket">[</span><a href="/w/index.php?title=Good&amp;action=edit&amp;section=19" title="Edit section: History and novelty">edit</a><span class="mw-editsection-bracket">]</span></span></h3> <table role="presentation" class="mbox-small plainlinks sistersitebox" style="background-color:#f9f9f9;border:1px solid #aaa;color:#000"> <tr> <td class="mbox-image"><img alt="" src="//upload.wikimedia.org/wikipedia/commons/thumb/9/99/Wiktionary-logo-en-v2.svg/40px-Wiktionary-logo-en-v2.svg.png" width="40" height="40" class="noviewer" srcset="//upload.wikimedia.org/wikipedia/commons/thumb/9/99/Wiktionary-logo-en-v2.svg/60px-Wiktionary-logo-en-v2.svg.png 1.5x, //upload.wikimedia.org/wikipedia/commons/thumb/9/99/Wiktionary-logo-en-v2.svg/80px-Wiktionary-logo-en-v2.svg.png 2x" data-file-width="391" data-file-height="391" /></td> <td class="mbox-text plainlist">Look up <i><b><a href="https://en.wiktionary.org/wiki/goodness" class="extiw" title="wiktionary:goodness">goodness</a></b></i>&#160;or <i><b><a href="https://en.wiktionary.org/wiki/evil" class="extiw" title="wiktionary:evil">evil</a></b></i> in Wiktionary, the free dictionary.</td> </tr> </table> <table role="presentation" class="mbox-small plainlinks sistersitebox" style="background-color:#f9f9f9;border:1px solid #aaa;color:#000"> <tr> <td class="mbox-image"><img alt="" src="//upload.wikimedia.org/wikipedia/commons/thumb/f/fa/Wikiquote-logo.svg/34px-Wikiquote-logo.svg.png" width="34" height="40" class="noviewer" srcset="//upload.wikimedia.org/wikipedia/commons/thumb/f/fa/Wikiquote-logo.svg/51px-Wikiquote-logo.svg.png 1.5x, //upload.wikimedia.org/wikipedia/commons/thumb/f/fa/Wikiquote-logo.svg/68px-Wikiquote-logo.svg.png 2x" data-file-width="300" data-file-height="355" /></td> <td class="mbox-text plainlist">Wikiquote has quotations related to: <i><b><a href="https://en.wikiquote.org/wiki/goodness" class="extiw" title="q:goodness">goodness</a></b></i></td> </tr> </table> <table role="presentation" class="mbox-small plainlinks sistersitebox" style="background-color:#f9f9f9;border:1px solid #aaa;color:#000"> <tr> <td class="mbox-image"><img alt="" src="//upload.wikimedia.org/wikipedia/commons/thumb/f/fa/Wikiquote-logo.svg/34px-Wikiquote-logo.svg.png" width="34" height="40" class="noviewer" srcset="//upload.wikimedia.org/wikipedia/commons/thumb/f/fa/Wikiquote-logo.svg/51px-Wikiquote-logo.svg.png 1.5x, //upload.wikimedia.org/wikipedia/commons/thumb/f/fa/Wikiquote-logo.svg/68px-Wikiquote-logo.svg.png 2x" data-file-width="300" data-file-height="355" /></td> <td class="mbox-text plainlist">Wikiquote has quotations related to: <i><b><a href="https://en.wikiquote.org/wiki/evil" class="extiw" title="q:evil">evil</a></b></i></td> </tr> </table> <p>An event is often seen as being of value simply because of its <i>novelty</i> in fashion and art. By contrast, cultural history and other antiques are sometimes seen as of value in and of themselves due to their <i>age</i>. Philosopher-historians Will and Ariel Durant spoke as much with the quote, "As the sanity of the individual lies in the continuity of his memories, so the sanity of the group lies in the continuity of its traditions; in either case a break in the chain invites a neurotic reaction" (The Lessons of History, 72).</p> <p>Assessment of the value of old or historical artifacts takes into consideration, especially but not exclusively: the value placed on having a detailed knowledge of the past, the desire to have tangible ties to ancestral history, and/or the increased market value scarce items traditionally hold.</p> <p><a href="/wiki/Creativity" title="Creativity">Creativity</a> and <a href="/wiki/Innovation" title="Innovation">innovation</a> and <a href="/wiki/Invention" title="Invention">invention</a> are sometimes upheld as fundamentally good especially in Western industrial society&#160;— all imply newness, and even opportunity to profit from novelty. <a href="/wiki/Bertrand_Russell" title="Bertrand Russell">Bertrand Russell</a> was notably pessimistic about creativity and thought that <a href="/wiki/Knowledge" title="Knowledge">knowledge</a> expanding faster than <a href="/wiki/Wisdom" title="Wisdom">wisdom</a> necessarily was fatal.</p> <h2><span class="mw-headline" id="Goodness_and_morality_in_biology">Goodness and morality in biology</span><span class="mw-editsection"><span class="mw-editsection-bracket">[</span><a href="/w/index.php?title=Good&amp;action=edit&amp;section=20" title="Edit section: Goodness and morality in biology">edit</a><span class="mw-editsection-bracket">]</span></span></h2> <p>The issue of good and evil in the human visuality, often associated with <a href="/wiki/Morality" title="Morality">morality</a>, is regarded by some biologists (notably <a href="/wiki/E._O._Wilson" title="E. O. Wilson">Edward O. Wilson</a>, <a href="/wiki/Jeremy_Griffith" title="Jeremy Griffith">Jeremy Griffith</a>, <a href="/wiki/David_Sloan_Wilson" title="David Sloan Wilson">David Sloan Wilson</a> and <a href="/wiki/Frans_de_Waal" title="Frans de Waal">Frans de Waal</a>) as an important question to be addressed by the field of biology.<sup id="cite_ref-19" class="reference"><a href="#cite_note-19">[19]</a></sup><sup id="cite_ref-20" class="reference"><a href="#cite_note-20">[20]</a></sup><sup id="cite_ref-21" class="reference"><a href="#cite_note-21">[21]</a></sup><sup id="cite_ref-22" class="reference"><a href="#cite_note-22">[22]</a></sup></p> <h2><span class="mw-headline" id="See_also">See also</span><span class="mw-editsection"><span class="mw-editsection-bracket">[</span><a href="/w/index.php?title=Good&amp;action=edit&amp;section=21" title="Edit section: See also">edit</a><span class="mw-editsection-bracket">]</span></span></h2> <div role="navigation" aria-label="Portals" class="noprint portal plainlist tright" style="margin:0.5em 0 0.5em 1em;border:solid #aaa 1px"> <ul style="display:table;box-sizing:border-box;padding:0.1em;max-width:175px;background:#f9f9f9;font-size:85%;line-height:110%;font-style:italic;font-weight:bold"> <li style="display:table-row"><span style="display:table-cell;padding:0.2em;vertical-align:middle;text-align:center"><img alt="" src="//upload.wikimedia.org/wikipedia/commons/thumb/c/cd/Socrates.png/18px-Socrates.png" width="18" height="28" class="noviewer" srcset="//upload.wikimedia.org/wikipedia/commons/thumb/c/cd/Socrates.png/27px-Socrates.png 1.5x, //upload.wikimedia.org/wikipedia/commons/thumb/c/cd/Socrates.png/36px-Socrates.png 2x" data-file-width="326" data-file-height="500" /></span><span style="display:table-cell;padding:0.2em 0.2em 0.2em 0.3em;vertical-align:middle"><a href="/wiki/Portal:Philosophy" title="Portal:Philosophy">Philosophy portal</a></span></li> </ul> </div> <div> <table class="multicol" role="presentation" style="border-collapse: collapse; padding: 0; border: 0; background:transparent; width:100%;"> <tr> <td style="width: 33.33%;text-align:left;vertical-align:top;"> <ul> <li><a href="/wiki/Adiaphora" title="Adiaphora">Adiaphora</a></li> <li><a href="/wiki/Axiology" title="Axiology">Axiology</a></li> <li><a href="/wiki/Beyond_Good_and_Evil" 