').style.display='block';  		setCookie("onlyStudent","0",12);  	}  }  var temp=getCookie("onlyStudent");  //此情况修改vagueSearch的checked状态  if(temp=="1"){    document.getElementById('onlyStudent').checked=true;  }  </script>    <div  id="content" style="border-bottom:1px solid #aaaaaa;">    <!--最大高度为105px,能显示5行多的样子-->  <div style="line-height:20px;float:none;margin-top:5px;margin-bottom:5px;margin-right:5px;padding:4px;background-color:#cccccc;border:1px solid #DCDDDD;max-height:105px;font-size:14px;overflow:auto;">  	<span style='float:left;display:block;'>Synonym:&nbsp;</span><span style='float:left;display:block;'><a href='javascript:;' onclick='q1("audience");'>audience</a>,&nbsp;</span><span style='float:left;display:block;'><a href='javascript:;' onclick='q1("building");'>building</a>,&nbsp;</span><span style='float:left;display:block;'><a href='javascript:;' onclick='q1("congregation");'>congregation</a>,&nbsp;</span><span style='float:left;display:block;'><a href='javascript:;' onclick='q1("dwelling");'>dwelling</a>,&nbsp;</span><span style='float:left;display:block;'><a href='javascript:;' onclick='q1("home");'>home</a>,&nbsp;</span><span style='float:left;display:block;'><a href='javascript:;' onclick='q1("lodge");'>lodge</a>,&nbsp;</span><span style='float:left;display:block;'><a href='javascript:;' onclick='q1("residence");'>residence</a>,&nbsp;</span><span style='float:left;display:block;'><a href='javascript:;' onclick='q1("shelter");'>shelter</a>.&nbsp;</span><span style='float:left;display:block;'>Similar words:&nbsp;</span><span style='float:left;display:block;'><a href='warehouse.html' target='_blank'>warehouse</a>,&nbsp;</span><span style='float:left;display:block;'><a href='household.html' target='_blank'>household</a>,&nbsp;</span><span style='float:left;display:block;'><a href='housing.html' target='_blank'>housing</a>,&nbsp;</span><span style='float:left;display:block;'><a href='the White House.html' target='_blank'>the White House</a>,&nbsp;</spa