isplay='block';  		setCookie("onlyStudent","0",12);  	}  }  var temp=getCookie("onlyStudent");  //此情况修改vagueSearch的checked状态  if(temp=="1"){    document.getElementById('onlyStudent').checked=true;  }  </script>    <div  id="content" style="border-bottom:1px solid #aaaaaa;">    <!--最大高度为105px,能显示5行多的样子-->  <div style="line-height:20px;float:none;margin-top:5px;margin-bottom:5px;margin-right:5px;padding:4px;background-color:#cccccc;border:1px solid #DCDDDD;max-height:105px;font-size:14px;overflow:auto;">  	<span style='float:left;display:block;'>Synonym:&nbsp;</span><span style='float:left;display:block;'><a href='javascript:;' onclick='q1("cross-examine");'>cross-examine</a>,&nbsp;</span><span style='float:left;display:block;'><a href='javascript:;' onclick='q1("examine");'>examine</a>,&nbsp;</span><span style='float:left;display:block;'><a href='javascript:;' onclick='q1("experiment");'>experiment</a>,&nbsp;</span><span style='float:left;display:block;'><a href='javascript:;' onclick='q1("grill");'>grill</a>,&nbsp;</span><span style='float:left;display:block;'><a href='javascript:;' onclick='q1("interrogate");'>interrogate</a>,&nbsp;</span><span style='float:left;display:block;'><a href='javascript:;' onclick='q1("prove");'>prove</a>,&nbsp;</span><span style='float:left;display:block;'><a href='javascript:;' onclick='q1("query");'>query</a>,&nbsp;</span><span style='float:left;display:block;'><a href='javascript:;' onclick='q1("question");'>question</a>,&nbsp;</span><span style='float:left;display:block;'><a href='javascript:;' onclick='q1("quiz");'>quiz</a>,&nbsp;</span><span style='float:left;display:block;'><a href='javascript:;' onclick='q1("try");'>try</a>,&nbsp;</span><span style='float:left;display:block;'><a href='javascript:;' onclick='q1("verify");'>verify</a>.&nbsp;</span><span style='float:left;display:block;'>Similar words:&nbsp;</span><span style='float:left;display:block;'><a href='protest.html' target='_blank'>protest</a>,&nbsp;</span><span style='fl